import React, { useState } from "react";
import AccountNav from "../components/AccountNav";
import OrdersList from "../components/OrdersList";
import DownloadsList from "../components/DownloadsList";
import "./m";

const sampleOrders = [
  { id: "1001", date: "2025-07-11", status: "Completed", total: "₹1,499.00" },
  { id: "1002", date: "2025-06-02", status: "Processing", total: "₹799.00" }
];

const sampleDownloads = [
  { id: "d1", name: "React Crash Course - PDF", expires: "2026-07-11" },
  { id: "d2", name: "JS Cheatsheet", expires: "Never" }
];

export default function MyAccount() {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [user, setUser] = useState({
    firstName: "Dhruv",
    lastName: "Chauhan",
    displayName: "dhruv_c",
    email: "dhruv@example.com"
  });

  function handleAccountSave(e) {
    e.preventDefault();
    // replace with API call
    alert("Account details saved (demo)");
  }

  function handleLogout() {
    // replace with real logout logic
    alert("Logged out (demo)");
  }

  return (
    <div className="myaccount-page">
      <header className="myaccount-hero">
        <div className="container">
          <h1>My Account</h1>
          <p>Manage your courses, downloads and account details</p>
        </div>
      </header>

      <main className="container myaccount-content" role="main">
        <AccountNav active={activeTab} onChange={setActiveTab} onLogout={handleLogout} />

        <section className="account-panel">
          {activeTab === "dashboard" && (
            <div>
              <h2>Dashboard</h2>
              <p>
                Hello <strong>{user.displayName}</strong> (not <strong>{user.displayName}</strong>?{" "}
                <button className="link-like" onClick={handleLogout}>Logout</button>)
              </p>
              <p>From your account dashboard you can view your recent orders, manage downloads, and edit your account details.</p>

              <div className="quick-cards">
                <div className="card">
                  <h3>Recent Orders</h3>
                  <OrdersList orders={sampleOrders} condensed />
                </div>

                <div className="card">
                  <h3>Available Downloads</h3>
                  <DownloadsList downloads={sampleDownloads} condensed />
                </div>
              </div>
            </div>
          )}

          {activeTab === "orders" && (
            <div>
              <h2>Orders</h2>
              <OrdersList orders={sampleOrders} />
            </div>
          )}

          {activeTab === "downloads" && (
            <div>
              <h2>Downloads</h2>
              <DownloadsList downloads={sampleDownloads} />
            </div>
          )}

          {activeTab === "addresses" && (
            <div>
              <h2>Addresses</h2>
              <div className="addresses">
                <form className="address-form" onSubmit={(e)=>{e.preventDefault(); alert("Saved (demo)")}}>
                  <h3>Billing Address</h3>
                  <label>Full name<input defaultValue="Dhruv Chauhan" /></label>
                  <label>Address line 1<input defaultValue="123 Example Street" /></label>
                  <label>City<input defaultValue="Mumbai" /></label>
                  <label>State / Province<input defaultValue="Maharashtra" /></label>
                  <label>Postal code<input defaultValue="400001" /></label>
                  <button className="btn">Save address</button>
                </form>

                <form className="address-form" onSubmit={(e)=>{e.preventDefault(); alert("Saved (demo)")}}>
                  <h3>Shipping Address</h3>
                  <label>Full name<input defaultValue="Dhruv Chauhan" /></label>
                  <label>Address line 1<input defaultValue="456 Another Lane" /></label>
                  <label>City<input defaultValue="Pune" /></label>
                  <label>State / Province<input defaultValue="Maharashtra" /></label>
                  <label>Postal code<input defaultValue="411001" /></label>
                  <button className="btn">Save address</button>
                </form>
              </div>
            </div>
          )}

          {activeTab === "account" && (
            <div>
              <h2>Account details</h2>
              <form className="account-form" onSubmit={handleAccountSave}>
                <div className="form-row">
                  <label>
                    First name
                    <input
                      value={user.firstName}
                      onChange={(e) => setUser({ ...user, firstName: e.target.value })}
                    />
                  </label>

                  <label>
                    Last name
                    <input
                      value={user.lastName}
                      onChange={(e) => setUser({ ...user, lastName: e.target.value })}
                    />
                  </label>
                </div>

                <label>
                  Display name
                  <input
                    value={user.displayName}
                    onChange={(e) => setUser({ ...user, displayName: e.target.value })}
                  />
                </label>

                <label>
                  Email address
                  <input
                    type="email"
                    value={user.email}
                    onChange={(e) => setUser({ ...user, email: e.target.value })}
                  />
                </label>

                <fieldset>
                  <legend>Change password</legend>
                  <label>Current password<input type="password" placeholder="••••••" /></label>
                  <label>New password<input type="password" placeholder="••••••" /></label>
                  <label>Confirm new password<input type="password" placeholder="••••••" /></label>
                </fieldset>

                <button className="btn primary" type="submit">Save changes</button>
              </form>
            </div>
          )}
        </section>
      </main>
    </div>
  );
}
