import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import CourseCard from "../components/CourseCard"; // Reuse existing CourseCard component
import "./InstructorDetailsPage.css";

export default function InstructorDetailsPage({ instructors, courses }) {
  const { id } = useParams();
  const navigate = useNavigate();
  const instructor = instructors.find(ins => ins.id === parseInt(id));

  if (!instructor) {
    return <p>Instructor not found.</p>;
  }

  const popularCourses = courses.filter(c => instructor.courseIds.includes(c.id));

  return (
    <div className="instr-details-page">
      <button className="back-btn" onClick={() => navigate(-1)}>‚Üê Back</button>
      <div className="instr-header">
        <img src={instructor.image} alt={instructor.name} className="instr-photo" />
        <div className="instr-info">
          <h1>{instructor.name}</h1>
          <p className="instr-title">{instructor.title}</p>
          <div className="instr-stats">
            <span>{instructor.students} Students</span>
            <span>({instructor.ratings} Ratings)</span>
          </div>
          <h3>About Me</h3>
          <p className="instr-bio">{instructor.bio}</p>
          <div className="contact-me">
            <h4>Contact Me</h4>
            <p><strong>Address:</strong> {instructor.address}</p>
            <p><strong>Email:</strong> {instructor.email}</p>
            <p><strong>Phone:</strong> {instructor.phone}</p>
          </div>
        </div>
      </div>

      <section className="popular-courses">
        <h2>My Popular Courses</h2>
        <div className="courses-grid">
          {popularCourses.map(course => (
            <CourseCard key={course.id} course={course} />
          ))}
        </div>
      </section>
    </div>
  );
}
