import React from "react";

export default function OrdersList({ orders = [], condensed = false }) {
  if (orders.length === 0) {
    return <p>No orders found.</p>;
  }

  if (condensed) {
    return (
      <ul className="mini-list">
        {orders.slice(0,3).map(o => (
          <li key={o.id}>
            <strong>#{o.id}</strong> — {o.date} — {o.total}
          </li>
        ))}
      </ul>
    );
  }

  return (
    <table className="orders-table" role="table">
      <thead>
        <tr>
          <th>Order</th>
          <th>Date</th>
          <th>Status</th>
          <th>Total</th>
          <th>Action</th>
        </tr>
      </thead>

      <tbody>
        {orders.map(o => (
          <tr key={o.id}>
            <td>#{o.id}</td>
            <td>{o.date}</td>
            <td>{o.status}</td>
            <td>{o.total}</td>
            <td><button className="btn small">View</button></td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
