import React from "react";

export default function DownloadsList({ downloads = [], condensed = false }) {
  if (!downloads.length) return <p>No downloads available.</p>;

  if (condensed) {
    return (
      <ul className="mini-list">
        {downloads.slice(0,3).map(d => <li key={d.id}>{d.name}</li>)}
      </ul>
    );
  }

  return (
    <ul className="downloads-list">
      {downloads.map(d => (
        <li key={d.id} className="download-item">
          <div>
            <strong>{d.name}</strong>
            <div className="muted">Expires: {d.expires}</div>
          </div>
          <div>
            <button className="btn small">Download</button>
          </div>
        </li>
      ))}
    </ul>
  );
}
